mapper.insert:
INSERT INTO USER_LOGIN (
    USER_ID,
    USER_SEQ,
    PASSWORD
)
VALUES (
    #{userId},
    #{userSeq},
    #{password}
)

mapper.select:
SELECT  USER_ID AS userId
FROM    USER_LOGIN
WHERE   USER_ID = #{userId}

SELECT  ${type}
FROM    USER
WHERE   ${type} = #{value}
    AND DELETE_YN = 'N'
